M=\begin{bmatrix}
  m_{1}&amp;amp;  0&amp;amp; 0\\
  0&amp;amp;  m_{2}&amp;amp; 0\\
  0&amp;amp;  0&amp;amp;  m_{3}
\end{bmatrix}\\
C=\begin{bmatrix}
  c_{1}+c_{2}&amp;amp;  -c_{2}&amp;amp; 0\\
  -c_{2}&amp;amp;  c_{2}+c_{3}&amp;amp; -c_{3}\\
  0&amp;amp;  -c_{3}&amp;amp;  c_{3}
\end{bmatrix}\\
K=\begin{bmatrix}
  k_{1}+k_{2}&amp;amp;  -k_{2}&amp;amp; 0\\
  -k_{2}&amp;amp;  k_{2}+k_{3}&amp;amp; -k_{3}\\
  0&amp;amp;  -k_{3}&amp;amp;  k_{3}
\end{bmatrix}
